import React, { useState } from 'react';
import { useQuery, gql } from '@apollo/client';

const GET_TASKS = gql`
  query sortedTasks($token: String!) {
    tasks(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;

const AtoZ = () => {
  const [sortedTasks, setSortedTasks] = useState([]);
  const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
  const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));
  const { data: tasksData } = useQuery(GET_TASKS, {
    variables: {
      token: currentUserToken,
      userName: currentUserName
    }
  });

  function handle() {
    if (tasksData) {
      const tasksCopy = [...tasksData.tasks];
      const sortedTasks = tasksCopy.sort((a, b) => a.taskName.localeCompare(b.taskName));
      setSortedTasks(sortedTasks);
    }
  }


  return (
<button className="components" onClick={() => {
  handle();
  setIsSorted(!isSorted);
}}>
  {isSorted ? 'Sorted' : 'Sort'}
</button>

  );
};

export default AtoZ;
