import {useQuery,gql,useMutation} from '@apollo/client'
import ReactDOM from 'react-dom';
import Modal from 'react-modal';

const GET_TASKS = gql`
  query tasks($token: String!) {
    tasks(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;

const DELETE_TASKS = gql`
  mutation DeleteAllTasks($id: [ID!]!, $token: String!) {
    deleteAllTasks(id: $id, token: $token)
  }
`;
Modal.setAppElement('root');

export default function ExpoButton(){
  const [deleteAllTasksMutation] = useMutation(DELETE_TASKS)
  const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
  const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));
  const { data: tasksData } = useQuery(GET_TASKS,{    
      variables: {
        token: currentUserToken,
        userName: currentUserName
      }
  });

  let subtitle;
  const [modalIsOpen, setIsOpen] = React.useState(false);

  function openModal() {
    setIsOpen(true);
  }

  function closeModal() {
    setIsOpen(false);
  }

  async function removeAllTasks() {
    const taskIds = tasksData.tasks.map((task) => task.id);
    await deleteAllTasksMutation({
      variables: {
        token: currentUserToken,
        id: taskIds
      },
      update: (cache) => {
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: [] }, 
        });
      },
    });
  }



    return(
      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
      <button className="DeleteAllButton" >
        <svg xmlns="http://www.w3.org/2000/svg" onClick={openModal} className="DeleteAllButton" height="1em" viewBox="0 0 448 512">
         <path d="M135.2 17.7L128 32H32C14.3 32 0 46.3 0 64S14.3 96 32 96H416c17.7 0 32-14.3 32-32s-14.3-32-32-32H320l-7.2-14.3C307.4 6.8 296.3 0 284.2 0H163.8c-12.1 0-23.2 6.8-28.6 17.7zM416 128H32L53.2 467c1.6 25.3 22.6 45 47.9 45H346.9c25.3 0 46.3-19.7 47.9-45L416 128z"/></svg>
         <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Hello</h2>
        <button onClick={closeModal}>close</button>
        <div>I am a modal</div>
        <form>
          <input />
          <button>tab navigation</button>
          <button>stays</button>
          <button>inside</button>
          <button>the modal</button>
        </form>
      </button>   
      </Modal>
    )
}
