import './components.css';
import { useNavigate } from 'react-router-dom';
import {useQuery,gql,useMutation} from '@apollo/client'

const GET_TASKS = gql`
  query tasks {
    tasks {
      name
      done
      id
    }
  }
`;

const GET_USER = gql`
  query users {
    users {
      name
      tasks{
        name
        done
        id
      }
      token
      isLoggedIn
    }
  }
`;

const NEW_USER = gql`
  mutation newUser($token: String!, $name: String!, $isLoggedIn: Boolean!, $tasks: [userInput]) {
    newUser(token: $token, name: $name, isLoggedIn: $isLoggedIn, tasks: $tasks) {
      name
      tasks {
        name
        done
        id
      }
      token
      isLoggedIn
    }
  }
`;


