import { GoogleLogin, GoogleOAuthProvider } from '@react-oauth/google';
import jwt_decode from 'jwt-decode';
import { useState, useEffect } from 'react';
import './components.css';
import { useNavigate } from 'react-router-dom';
import  {gql,useMutation} from '@apollo/client'
import client from '../index'
import userimage from './icons/UserImg.svg'

const CHECK_USER = gql`
  query CheckUser($Uid: String!) {
    CheckUser(Uid: $Uid) {
      _id
      userName
    }
  }
`;

export default function Login() {
    const navigate = useNavigate();
    const [addGoogleuserMutation] = useMutation(CREATE_GOOGLE_USER);
    const [addLocalUserMutation] = useMutation(CREATE_LOCAL_USER);
    const [username, setUsername] = useState('');
    const [currentUserUid, setCurrentUserUid] = useState(null);
    const [currentUserName, setCurrentUserName] = useState(null);
    const [password, setPassword] = useState('');
    const [currentUserImg, setCurrentUserImg] = useState(null);
    const [isRegistered, setIsRegistered] = useState(false); 
    const [loggedWithGoogle,setLoggedWithGoogle] = useState(false)
    const [loggedWithLocal,setLoggedWithLocal] = useState(false)
    const userImg = userimage

    useEffect(() => {
      if(currentUserUid !== null){
        console.log('user:' + currentUserUid);
      }else{
        console.log('user:' + currentUserName);
      }
    }, []);

    const handleSuccess = async (credentialResponse) => {
      var decoded = jwt_decode(credentialResponse.credential);
      const Uid = decoded.sub;
      const { data } = await client.query({
        query: CHECK_USER,
        variables: { Uid: Uid }
      });
      console.log(data);
      if (!data.CheckGoogleUser) {
        await addGoogleuserMutation({
          variables: {
            Uid: Uid,
            name: decoded.name,
            email: decoded.email
          },
          update(cache, { data }) {
            const newUserData = {
              _id: data.newGoogleUser._id,
              userName: data.newGoogleUser.userName,
            }
            console.log(data);
            const updated = {
              CheckGoogleUser: { ...data.CheckGoogleUser, ...newUserData }
            };
            client.writeQuery({
              query: CHECK_USER,
              variables: { Uid: Uid },
              data: updated
            });
          }
        });
        setLoggedWithGoogle(true)
        localStorage.setItem('LoggedWithGoogle', JSON.stringify(true));
        localStorage.setItem('currentUser', JSON.stringify(decoded.sub));
        localStorage.setItem('currentUserName', JSON.stringify(decoded.given_name));
        localStorage.setItem('currentUserImg', JSON.stringify(decoded.picture));
        navigate('/');
      } else {
        setIsRegistered(true);
      }
    };

    const handleUserData = async () => {
      const { data } = await client.query({
        query: CHECK_LOCAL_USER,
        variables: { username: username, password:password }
      });
      if (!data.CheckLocalUser) {
        await addLocalUserMutation({
          variables: {
            username:username,
            password:password
          },
          update(cache, { data }) {
            const newUserData = {
              username: data.newLocalUser.username,
              password: data.newLocalUser.password,
            }
            console.log(data);
            const updated = {
              CheckLocalUser: { ...data.CheckLocalUser, ...newUserData }
            };
            client.writeQuery({
              query: CHECK_LOCAL_USER,
              variables: { username:username },
              data: updated
            });
          }
        });
        setLoggedWithLocal(true);
        localStorage.setItem('LoggedWithLocal', JSON.stringify(true));
        localStorage.setItem('currentUserName', JSON.stringify(username));
        localStorage.setItem('currentUserImg', JSON.stringify(userImg));
        navigate('/')
      } else {
        setIsRegistered(true);
      }
    }

    if (isRegistered) {
      navigate('/Error');
    } else {
      return (
        <div>
          <div className='input-container'>
            <label className='input-label'>Username</label>
            <input
            type='text'
            className='input-field'
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className='input-container'>
            <label className='input-label'>Password</label>
            <input
              type='password'
              className='input-field'
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className='button-container'>
            <button className='submit-button' onClick={handleUserData}>Submit</button>
          </div>
          <div className='center-text'>
            <label className='google-label'>O registrati con Google...</label>
          </div>
          <div id="signInButton" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <GoogleOAuthProvider clientId="1038509275601-a2gf0vtu5pt81as8586gt0hsqrqis0mh.apps.googleusercontent.com">
              <GoogleLogin onSuccess={handleSuccess} />
            </GoogleOAuthProvider>
          </div>
        </div>
      );
    }    
  }
