import React, { useState } from 'react';
import { useQuery, gql,useMutation } from '@apollo/client';

const GET_TASKS = gql`
  query tasks($token: String!) {
    tasks(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;

const TASK_SORTED = gql`
  mutation getSortedByNotDone($token: String!) {
    getSortedByNotDone(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;

export default function Notdone(){
  const [sortedTasks, setSortedTasks] = useState([]);
  const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
  const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));
  const [getTasks] = useMutation(TASK_SORTED)
  const { data: tasksData } = useQuery(GET_TASKS, {
    variables: {
      token: currentUserToken,
      userName: currentUserName
    }
  });

  function handle() {
    getTasks({
      variables: {
        token: currentUserToken,
        userName: currentUserName
      },
      update: (cache, { data }) => {
        const sorted = data.getSortedByNotDone;
        setSortedTasks(sorted); 
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: sorted }
        });
      }
    })
  }


    return(
      <button className="components" onClick={handle}>
       <svg className="componentssvg" xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 512 512">
        <path d="M256 512A256 256 0 1 0 256 0a256 256 0 1 0 0 512zm0-384c13.3 0 24 10.7 24 24V264c0 13.3-10.7 24-24 24s-24-10.7-24-24V152c0-13.3 10.7-24 24-24zM224 352a32 32 0 1 1 64 0 32 32 0 1 1 -64 0z"/>
      </svg>
      </button>   
    )
}
