import {gql} from '@apollo/client'

const GET_TASKS = gql`
  query tasks($token: String!) {
    tasks(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;

const ADD_TASK = gql`
  mutation newTask($input: taskInput!, $token: String!, $userName: String!) {
    newTask(input: $input, token: $token, userName: $userName) {
      userName
      token
      taskName
      id
    }
  }
`;

const TASK_DONE = gql`
  mutation updatedTask($id: ID!, $token: String!) {
    updatedTask(id: $id, token: $token) 
  }
`;

const DELETE_TASK = gql`
  mutation deletedTask($id: ID!, $token: String!) {
    deletedTask(id: $id, token: $token) 
  }
`;

const UPDATE_TITLE = gql`
mutation newupdatedTitleTask($id: ID!, $input: titleInput!, $token: String!) {
  updatedTitle(id: $id, input: $input, token: $token)
}
`;

const DELETE_TASKS = gql`
  mutation DeleteAllTasks($id: [ID!]!, $token: String!) {
    deleteAllTasks(id: $id, token: $token)
  }
`;

const TASK_SORTED = gql`
  mutation getSortedTasks($token: String!) {
    getSortedTasks(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;

const TASK_SORTED_DESC = gql`
  mutation getNonSortedTasks($token: String!) {
    getNonSortedTasks(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;

const TASK_SORTED_DONE = gql`
  mutation getSortedByDone($token: String!) {
    getSortedByDone(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;

const TASK_SORTED_NOT_DONE = gql`
  mutation getSortedByNotDone($token: String!) {
    getSortedByNotDone(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;


export {
    GET_TASKS,
    ADD_TASK,
    TASK_DONE,
    DELETE_TASK,
    UPDATE_TITLE,
    DELETE_TASKS,
    TASK_SORTED,
    TASK_SORTED_DESC,
    TASK_SORTED_DONE,
    TASK_SORTED_NOT_DONE
  };
