import './App.css';
import Task from './Task';
import React from 'react';
import TaskForm from './TaskForm';
import { useEffect,useState,useRef } from "react";
import {useQuery,gql,useMutation} from '@apollo/client'
import { googleLogout } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';
import  ExpoButton  from './components/ExpoButton.js'
import  DeleteAll from './components/DeleteAll.js'
import  FilterButton from './components/FilterButton'
import {CSSTransition} from 'react-transition-group'
import AtoZ from './components/AtoZ'
import ZtoA from './components/ZtoA'
import Done from './components/Done'
import NotDone from './components/NotDone'

const GET_TASKS = gql`
  query tasks($token: String!) {
    tasks(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;

const ADD_TASK = gql`
  mutation newTask($input: taskInput!, $token: String!, $userName: String!) {
    newTask(input: $input, token: $token, userName: $userName) {
      userName
      token
      taskName
      id
    }
  }
`;

const TASK_DONE = gql`
  mutation updatedTask($id: ID!, $token: String!) {
    updatedTask(id: $id, token: $token) 
  }
`;

const DELETE_TASK = gql`
  mutation deletedTask($id: ID!, $token: String!) {
    deletedTask(id: $id, token: $token) 
  }
`;

const UPDATE_TITLE = gql`
mutation newupdatedTitleTask($id: ID!, $input: titleInput!, $token: String!) {
  updatedTitle(id: $id, input: $input, token: $token)
}
`;

function App() {
  const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
  const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));
  const currentUserImg = JSON.parse(localStorage.getItem('currentUserImg'));
  const { data: tasksData } = useQuery(GET_TASKS,{    
    variables: {
    token: currentUserToken,
    userName: currentUserName
  }
});

  const ref = useRef();
  const [updateTitleMutation] = useMutation(UPDATE_TITLE)
  const [addTaskMutation] = useMutation(ADD_TASK)
  const [deletedTaskMutation] = useMutation(DELETE_TASK)
  const [updateTaskDoneMutation] = useMutation(TASK_DONE)
  const [open, setOpen] = useState(false);
  const tasks = tasksData?.tasks ?? [];
  const numberComplete = tasks.filter(t => t.done).length;
  const numberTotal = tasks.length;
  const navigate = useNavigate();

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
    console.log('action from user:' + currentUserToken);
  },[tasks]);

  useEffect(() => {
    function handleClickOutside(event) {
      if (ref.current && !ref.current.contains(event.target)) {
        setOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };

  }, []);

  function AddTask(taskName) {
    addTaskMutation({
      variables: {
        input: {
          name: taskName
        },
        token: currentUserToken,
        userName: currentUserName
      },
      update: (cache, { data }) => {
        const existingTasks = cache.readQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          }
        });
        const newTask = {
          userName: currentUserName,
          token: currentUserToken,
          taskName: data.newTask.taskName,
          done: false,
          id: data.newTask.id
        };
        const updatedTasks = [...existingTasks.tasks, newTask];
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: updatedTasks }
        });
      },
    });
  }

  function removeTask(index) {
    const taskId = tasksData.tasks[index].id; 
    deletedTaskMutation({
      variables: {
        id: taskId,
        token: currentUserToken
      },
      update: (cache) => {
        const existingTasks = cache.readQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          }
        });
        const updatedTasks = existingTasks.tasks.filter((task) => task.id !== taskId);
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: updatedTasks }
        });
      },
    });
  }

  function updateTaskDone(index, done) {
    const taskId = tasksData.tasks[index].id;
    updateTaskDoneMutation({
      variables: {
        id: taskId,
        token: currentUserToken,
        done:done
      },
      update: (cache) => {
        const existingTasks = cache.readQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          }
        });
        const updatedTasks = existingTasks.tasks.map((task, i) => {
          if (i === index) {
            return { ...task, done };
          }
          return task;
        });
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: updatedTasks }
        });
      }
    });
  }

  function renameTasks(index, newName) {
    const taskId = tasksData.tasks[index].id;
    updateTitleMutation({
      variables: {
        id: taskId,
        token: currentUserToken,
        input: {
          name: newName
        }
      },
      update: (cache, { data }) => {
        const existingTasks = cache.readQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          }
        });
        const updatedTasks = existingTasks.tasks.map(task => {
          if (task.id === taskId) {
            return { ...task, taskName: newName };
          }
          return task;
        });
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: updatedTasks }
        });
      }
    }).catch(error => {
      if (
        error.message.includes('version_conflict_engine_exception') &&
        error.message.includes('version conflict')
      ) {
      } else {
        console.error(error);
      }
    });
  }  

