import {useQuery,gql,useMutation} from '@apollo/client'

const GET_TASKS = gql`
  query tasks($token: String!) {
    tasks(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;

const DELETE_TASK = gql`
  mutation deletedTask($id: ID!, $token: String!) {
    deletedTask(id: $id, token: $token) 
  }
`;

export default function ExpoButton(){
    const [deletedTaskMutation] = useMutation(DELETE_TASK)
    const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
    const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));
    const { data: tasksData } = useQuery(GET_TASKS,{    
        variables: {
        token: currentUserToken,
        userName: currentUserName
      }
    });

    function removeAllTasks() {
        const deletedTasks = tasksData.tasks.map((task) => {
          deletedTaskMutation({
            variables: {
              id: task.id,
              token: currentUserToken
            },
            update: (cache) => {
              const existingTasks = cache.readQuery({
                query: GET_TASKS,
                variables: {
                  token: currentUserToken,
                  userName: currentUserName
                }
              });
              const updatedTasks = existingTasks.tasks.filter((t) => t.id !== task.id);
              cache.writeQuery({
                query: GET_TASKS,
                variables: {
                  token: currentUserToken,
                  userName: currentUserName
                },
                data: { tasks: updatedTasks }
              });
            },
          });
          return task.id;
        });
    }

    return(
      <button className="DeleteAllButton" >
        <svg xmlns="http://www.w3.org/2000/svg" onClick={removeAllTasks} className="DeleteAllButton" height="1em" viewBox="0 0 448 512">
         <path d="M135.2 17.7L128 32H32C14.3 32 0 46.3 0 64S14.3 96 32 96H416c17.7 0 32-14.3 32-32s-14.3-32-32-32H320l-7.2-14.3C307.4 6.8 296.3 0 284.2 0H163.8c-12.1 0-23.2 6.8-28.6 17.7zM416 128H32L53.2 467c1.6 25.3 22.6 45 47.9 45H346.9c25.3 0 46.3-19.7 47.9-45L416 128z"/></svg>
      </button>   
    )
}
