          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: sorted }
        });
      }
    })
  }

  function Navbar(props) {
    return (
      <div>
        <nav className="nav">
          <ul className="nav-nav">{props.children}</ul>
          <p className="user">Ciao {currentUserName}</p>
        </nav>
      </div>
    );
  }

  function Navitem(props) {
    return (
      <li className="nav-item">
        <a href="#" className="icon-button" onClick={() => setOpen(!open)}>
          <img src={currentUserImg}  referrerPolicy="no-referrer" className="UserImg" alt="Immagine del profilo" />
          {props.icon}
        </a>
        {open && props.children}
      </li>
    );
  }

  function DropdownMenu() {
    const [activeMenu,setActiveMenu] = useState('main')
    const [menuHeight,setMenuHeight] = useState(null)
    const dropdownRef = useRef(null);

    useEffect(() => {
      setMenuHeight(dropdownRef.current?.firstChild.offsetHeight)
    }, [])


    function calcHeight(el) {
      const height = el.offsetHeight;
      setMenuHeight(height);
    }

    function DropdownItem(props) {
      const onClick = (event) => {
        event.preventDefault();
        if (props.goToMenu) {
          setActiveMenu(props.goToMenu);
        }
        if (props.onClick) {
          props.onClick();
        }
      };

      return (
        <a href="#" className="menu-item" onClick={onClick}>
          <span className="icon-button">{props.leftIcon}</span>
          {props.children}
          <span className="icon-right">{props.rightIcon}</span>
        </a>
      );
    }



    return (
      <div className="dropdown" style={{ height: menuHeight }} ref={dropdownRef}>

        <CSSTransition
          in={activeMenu === 'main'}
          timeout={500}
          classNames="menu-primary"
          unmountOnExit
          onEnter={calcHeight}>
          <div className="menu">
            <DropdownItem 
              rightIcon={<DeleteAll/>}
              onClick={removeAllTasks}>
              Elimina Tutto
              </DropdownItem>
            <DropdownItem
              rightIcon={<ExpoButton/>}
              onClick={handleExport}>
              Esporta JSON
            </DropdownItem>
            <DropdownItem
              rightIcon={<FilterButton/>}
              goToMenu="filtri">
              Filtra
            </DropdownItem>
            <DropdownItem
              rightIcon={<Logout/>}
              onClick={handleLogout}>
              Cambia Account
            </DropdownItem>

          </div>
        </CSSTransition>

        <CSSTransition
        in={activeMenu === 'filtri'}
        timeout={500}
        classNames="menu-secondary"
        unmountOnExit
        onEnter={calcHeight}>
        <div className="menu">
          <DropdownItem
          rightIcon={<AtoZ/>}
          onClick={handleSorted}
          > 
          Da A a Z
          </DropdownItem>
          <DropdownItem
          rightIcon={<ZtoA/>}
          onClick={handleSortedDesc}
          > 
          Da Z a A
          </DropdownItem>
          <DropdownItem
          rightIcon={<Completed/>}
          onClick={handleSortedDone}
          > Completate
          </DropdownItem>
          <DropdownItem
          rightIcon={<NotCompleted/>}
          onClick={handleNotDone}
          > 
          Da completare
          </DropdownItem>
          <DropdownItem 
          goToMenu="main"
          rightIcon={<GoBack/>}>
          Torna indietro
          </DropdownItem>

        </div>
      </CSSTransition>

      </div>
    );
  }

