import { GET_TASKS } from "../../queries/query";
import  { useMutation } from '@apollo/client'

const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));

function handleSortedDesc() {
  const [getTasksDESC] = useMutation(TASK_SORTED_DESC)

    getTasksDESC({
      variables: {
        token: currentUserToken,
        userName: currentUserName
      },
      update: (cache, { data }) => {
        const sorted = data.getNonSortedTasks;
        setSortedTasks(sorted);
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: sorted }
        });
      }
    })
  }

  export default handleSortedDesc
