function handleSorted() {
    getTasks({
      variables: {
        token: currentUserToken,
        userName: currentUserName
      },
      update: (cache, { data }) => {
        const sorted = data.getSortedTasks;
        setSortedTasks(sorted); 
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: sorted }
        });
      }
    })
  }

export default handleSorted
