import { GET_TASKS,TASK_SORTED_DONE } from "../../queries/query";
import { useState } from "react";
import { useMutation } from '@apollo/client'

const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));

function handleSorted() {
  const [sortedTasks, setSortedTasks] = useState([]);
  const [getTasksDone] = useMutation(TASK_SORTED_DONE)

    getTasks({
      variables: {
        token: currentUserToken,
        userName: currentUserName
      },
      update: (cache, { data }) => {
        const sorted = data.getSortedTasks;
        setSortedTasks(sorted); 
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: sorted }
        });
      }
    })
  }

export default handleSorted
