async function removeAllTasks() {
    const taskIds = tasksData.tasks.map((task) => task.id);
    await deleteAllTasksMutation({
      variables: {
        token: currentUserToken,
        id: taskIds
      },
      update: (cache) => {
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: [] }, 
        });
      },
    });
  }

export default removeAllTasks
