import './App.css';
import Task from './Task';
import React from 'react';
import TaskForm from './TaskForm';
import { useEffect } from "react";
import {useQuery,gql,useMutation} from '@apollo/client'
import { googleLogout } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';


const GET_TASKS = gql`
  query tasks {
    tasks {
      name
      done
      id
    }
  }
`;

const GET_USERS = gql`
  query users {
    users {
      name
      token
      tasks{
        name
        done
        id
      } 
    }
  }
`;

const GET_USER = gql`
  query user($token: String!) {
    user(token: $token) {
      name
      token
      tasks {
        name
        done
        id
      } 
    }
  }
`;

const ADD_TASK = gql`
  mutation newTask($input: taskInput!, $token: String!) {
    newTask(input: $input, token: $token) {
      name
      id
    }
  }
`;

const TASK_DONE = gql`
  mutation updatedTask($id: ID!, $token: String!) {
    updatedTask(id: $id, token: $token) 
  }
`;


const DELETE_TASK = gql`
  mutation deletedTask($id: ID!, $token: String!) {
    deletedTask(id: $id, token: $token) 
  }
`;

const UPDATE_TITLE = gql`
mutation newupdatedTitleTask($id: ID!, $input: titleInput!, $token: String!) {
  updatedTitle(id: $id, input: $input, token: $token)
}
`;


function App() {
  const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
  const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));
  const currentUserImg = JSON.parse(localStorage.getItem('currentUserImg'));
  const { data: tasksData } = useQuery(GET_TASKS)
  const { data: usersData } = useQuery(GET_USERS)
  const { data: userData } = useQuery(GET_USER, {
    variables: {
      token: currentUserToken
    }
  });

  const [updateTitleMutation] = useMutation(UPDATE_TITLE)
  const [addTaskMutation] = useMutation(ADD_TASK)
  const [deletedTaskMutation] = useMutation(DELETE_TASK)
  const [updateTaskDoneMutation] = useMutation(TASK_DONE)
  const tasks = userData?.user?.tasks ?? [];
  const user = userData?.user ?? []; 


  const navigate = useNavigate();

  useEffect(() => {
    localStorage.setItem('user', JSON.stringify(user));
    console.log('action from user:' + currentUserToken);
  }, [user]);

  function AddTask(name) {
    addTaskMutation({
      variables: {
        input: {
          name: name
        },
        token: currentUserToken
      },
      update: (cache, { data }) => {

        // GET_TASKS
        const existingTasks = cache.readQuery({ query: GET_TASKS });
        const newTask = {
          name: data.newTask.name,
          done: false,
          id: data.newTask.id,
        };
        const updatedTasks = [...existingTasks.tasks, newTask];
        cache.writeQuery({
          query: GET_TASKS,
          data: { tasks: updatedTasks }
        });

        // GET_USERS
        const { users } = cache.readQuery({ query: GET_USERS });
        const currentUser = users.find(user => user.token === currentUserToken);
        cache.writeQuery({
          query: GET_USERS,
          data: {
            users: [
              ...users.filter(user => user.token !== currentUserToken),
              {
                ...currentUser,
                tasks: [...currentUser.tasks, newTask]
              }
            ]
          }
        });

        // GET_USER
        cache.writeQuery({
          query: GET_USER,
          variables: {
            token: currentUserToken
          },
          data: {
            user: {
              ...currentUser,
              tasks: [...currentUser.tasks, newTask]
            }
          }
        });
      },
    });
  }

  function removeTask(index) {
    const taskId = tasksData.tasks[index].id;
    deletedTaskMutation({
      variables: {
        id: taskId,
        token: currentUserToken
      },
      update: (cache) => {
        // GET_TASKS
        const existingTasks = cache.readQuery({ query: GET_TASKS });
        const updatedTasks = existingTasks.tasks.filter((task, i) => i !== index);
        cache.writeQuery({
          query: GET_TASKS,
          data: { tasks: updatedTasks }
        });

        // GET_USERS
        const { users } = cache.readQuery({ query: GET_USERS });
        const currentUser = users.find(user => user.token === currentUserToken);
        cache.writeQuery({
          query: GET_USERS,
          data: {
            users: [
              ...users.filter(user => user.token !== currentUserToken),
              {
                ...currentUser,
                tasks: currentUser.tasks.filter(task => task.id !== taskId)
              }
            ]
          }
        });

        // GET_USER
        cache.writeQuery({
          query: GET_USER,
          variables: {
            token: currentUserToken
          },
          data: {
            user: {
              ...currentUser,
              tasks: currentUser.tasks.filter(task => task.id !== taskId)
            }
          }
        });
      }
    });
  }

  function updateTaskDone(index, done) {
    const taskId = tasksData.tasks[index].id;
    updateTaskDoneMutation({
      variables: {
        id: taskId,
        token: currentUserToken,
        done:done
      },
      update: (cache) => {
        // GET_TASKS
        const existingTasks = cache.readQuery({ query: GET_TASKS });
        const updatedTasks = existingTasks.tasks.map((task, i) => {
          if (i === index) {
            return { ...task, done };
          }
          return task;
        });
        cache.writeQuery({
          query: GET_TASKS,
          data: { tasks: updatedTasks }
        });

        // GET_USERS
        const { users } = cache.readQuery({ query: GET_USERS });
        const currentUser = users.find(user => user.token === currentUserToken);
        cache.writeQuery({
          query: GET_USERS,
          data: {
            users: [
              ...users.filter(user => user.token !== currentUserToken),
              {
                ...currentUser,
                tasks: updatedTasks
              }
            ]
          }
        });

        // GET_USER
        cache.writeQuery({
          query: GET_USER,
          variables: {
            token: currentUserToken
          },
          data: {
            user: {
              ...currentUser,
              tasks: updatedTasks
            }
          }
        });
      }
    });
  }

  function renameTasks(index, newName) {
    const taskId = tasksData.tasks[index].id;
    updateTitleMutation({
      variables: {
        id: taskId,
        token: currentUserToken,
        input: {
          name: newName
        }
      },
      update: (cache, { data }) => {
        const existingTasks = cache.readQuery({ query: GET_TASKS });
        const updatedTasks = existingTasks.tasks.map(task => {
          if (task.id === taskId) {
            return { ...task, name: newName };
          }
          return task;
        });
        cache.writeQuery({
          query: GET_TASKS,
          data: { tasks: updatedTasks }
        });

        const { users } = cache.readQuery({ query: GET_USERS });
        const currentUser = users.find(user => user.token === currentUserToken);
        cache.writeQuery({
          query: GET_USERS,
          data: {
            users: [
              ...users.filter(user => user.token !== currentUserToken),
              {
                ...currentUser,
                tasks: updatedTasks
              }
            ]
          }
        });

        // GET_USER
        cache.writeQuery({
          query: GET_USER,
          variables: {
            token: currentUserToken
          },
          data: {
            user: {
              ...currentUser,
              tasks: updatedTasks
            }
          }
        });
      }
    }).catch(error => {
      if (
        error.message.includes('version_conflict_engine_exception') &&
        error.message.includes('version conflict')
      ) {
      } else {
        console.error(error);
      }
    });
  }

  function getMessage() {
    const percentage = numberTotal === 0 ? 0 : (numberComplete / numberTotal) * 100;
    if (percentage === 0) {
      return 'no tasks complete ðŸ˜¾';
    }
    if (percentage === 100) {
      return 'nice job! ðŸ˜»';
    }
    return 'keep it up! ðŸ˜¼';
  }

  function handleLogout(){
    googleLogout()
    localStorage.setItem('currentUserImg', JSON.stringify(null));
    localStorage.setItem('currentUserName', JSON.stringify(null));
    localStorage.setItem('currentUser', JSON.stringify(null));
    navigate('/login');
  }

  const numberComplete = tasks.filter(t => t.done).length;
  const numberTotal = tasks.length;

//{tasksData && tasksData.tasks && tasksData.tasks.map((task, index) => (
  return (
    <main>
      <img src={currentUserImg} className="UserImg" alt="Immagine del profilo" />
      <h5 className="user">Ciao {currentUserName}</h5>
      <div className="container">
      <button className="out" onClick={handleLogout}>Disconnettiti</button>
    </div>
      <h1>{numberComplete}/{numberTotal} Complete</h1>
      <h2>{getMessage()}</h2>
      <TaskForm onAdd={AddTask}/>
      {tasks.map((task, index) => (
        <Task
          {...task}
          key={index}
          onRename={newName => renameTasks(index, newName)}
          onTrash={() => removeTask(index)}
          onToggle={done => updateTaskDone(index, done)}
        />
      ))}

    </main>
  )

}

export default App;
