import App from './App'
import './index.css'
import {InMemoryCache, ApolloProvider, ApolloClient} from '@apollo/client';
import { createRoot } from 'react-dom/client';
import {createBrowserRouter,RouterProvider,Route} from "react-router-dom"
import Login from './pages/Login'
const client = new ApolloClient({
  uri: 'http://localhost:5555/graphql',
   cache : new InMemoryCache({
    typePolicies: {
      Query: {
        fields: {
          tasks: {
            merge(existing = [], incoming) {
              // Identify the existing tasks by their unique ID
              const merged = existing.slice(0);

              // Merge the incoming tasks with the existing tasks
              for (let i = 0; i < incoming.length; ++i) {
                const incomingTask = incoming[i];
                const existingIndex = merged.findIndex(
                  (task) => task.id === incomingTask.id
                );

                // If the task exists in the cache, replace it with the incoming task
                if (existingIndex >= 0) {
                  merged[existingIndex] = incomingTask;
                }
                // If the task is new, add it to the merged array
                else {
                  merged.push(incomingTask);
                }
              }

              return merged;
            },
          },
        },
      },
    },
  })

});

const root = createRoot(document.getElementById('root'));

const router = createBrowserRouter([
    {
        path:'/',
        element:<App/>
    },    
    {
      path:'Login',
      element: <Login/>
    } 
])

root.render(
<ApolloProvider client={client}>
  <RouterProvider router={router}/>
</ApolloProvider>
);

export default client
