import App from './App'
import './index.css'
import {InMemoryCache, ApolloProvider, ApolloClient} from '@apollo/client';
import { createRoot } from 'react-dom/client';
import {createBrowserRouter,RouterProvider,Route} from "react-router-dom"
import Login from './pages/Login'
const client = new ApolloClient({
  uri: 'http://localhost:5555/graphql',
cache : new InMemoryCache({
    typePolicies: {
      // Configura il merge come false per tutti i tipi di dato
      // in modo che non venga eseguito alcun merge automatico
      // durante l'aggiornamento della cache
      Query: {
        fields: {
          // Configura il merge come false per tutti i campi della query
          // in modo che non venga eseguito alcun merge automatico
          // durante l'aggiornamento della cache
          tasks: {
            merge: false,
          },
          users: {
            merge: false,
          },
          user: {
            merge: false,
          },
        },
      },
    },
  })

});

const root = createRoot(document.getElementById('root'));

const router = createBrowserRouter([
    {
        path:'/',
        element:<App/>
    },    
    {
      path:'Login',
      element: <Login/>
    } 
])

root.render(
<ApolloProvider client={client}>
  <RouterProvider router={router}/>
</ApolloProvider>
);

export default client
