import './App.css';
import Task from './Task';
import React from 'react';
import TaskForm from './TaskForm';
import { useEffect,useState,useRef } from "react";
import {useQuery,gql,useMutation} from '@apollo/client'
import { googleLogout } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';

const GET_TASKS = gql`
  query tasks($token: String!) {
    tasks(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;

const ADD_TASK = gql`
  mutation newTask($input: taskInput!, $token: String!, $userName: String!) {
    newTask(input: $input, token: $token, userName: $userName) {
      userName
      token
      taskName
      id
    }
  }
`;

const TASK_DONE = gql`
  mutation updatedTask($id: ID!, $token: String!) {
    updatedTask(id: $id, token: $token) 
  }
`;

const DELETE_TASK = gql`
  mutation deletedTask($id: ID!, $token: String!) {
    deletedTask(id: $id, token: $token) 
  }
`;

const UPDATE_TITLE = gql`
mutation newupdatedTitleTask($id: ID!, $input: titleInput!, $token: String!) {
  updatedTitle(id: $id, input: $input, token: $token)
}
`;

function App() {
  const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
  const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));
  const currentUserImg = JSON.parse(localStorage.getItem('currentUserImg'));
  const { data: tasksData } = useQuery(GET_TASKS,{    
    variables: {
    token: currentUserToken,
    userName: currentUserName
  }
});

  const ref = useRef();
  const [updateTitleMutation] = useMutation(UPDATE_TITLE)
  const [addTaskMutation] = useMutation(ADD_TASK)
  const [deletedTaskMutation] = useMutation(DELETE_TASK)
  const [updateTaskDoneMutation] = useMutation(TASK_DONE)
  const [open, setOpen] = useState(false);
  const tasks = tasksData?.tasks ?? [];
  const numberComplete = tasks.filter(t => t.done).length;
  const numberTotal = tasks.length;
  const navigate = useNavigate();

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
    console.log('action from user:' + currentUserToken);
  },[tasks]);

  useEffect(() => {
    function handleClickOutside(event) {
      if (ref.current && !ref.current.contains(event.target)) {
        setOpen(false);
      }
    }

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);


  function AddTask(taskName) {
    addTaskMutation({
      variables: {
        input: {
          name: taskName
        },
        token: currentUserToken,
        userName: currentUserName
      },
      update: (cache, { data }) => {
        const existingTasks = cache.readQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          }
        });
        const newTask = {
          userName: currentUserName,
          token: currentUserToken,
          taskName: data.newTask.taskName,
          done: false,
          id: data.newTask.id
        };
        const updatedTasks = [...existingTasks.tasks, newTask];
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: updatedTasks }
        });
      },
    });
  }

  function removeTask(index) {
    const taskId = tasksData.tasks[index].id; 
    deletedTaskMutation({
      variables: {
        id: taskId,
        token: currentUserToken
      },
      update: (cache) => {
        const existingTasks = cache.readQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          }
        });
        const updatedTasks = existingTasks.tasks.filter((task) => task.id !== taskId);
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: updatedTasks }
        });
      },
    });
  }

  function updateTaskDone(index, done) {
    const taskId = tasksData.tasks[index].id;
    updateTaskDoneMutation({
      variables: {
        id: taskId,
        token: currentUserToken,
        done:done
      },
      update: (cache) => {
        const existingTasks = cache.readQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          }
        });
        const updatedTasks = existingTasks.tasks.map((task, i) => {
          if (i === index) {
            return { ...task, done };
          }
          return task;
        });
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: updatedTasks }
        });
      }
    });
  }

  function renameTasks(index, newName) {
    const taskId = tasksData.tasks[index].id;
    updateTitleMutation({
      variables: {
        id: taskId,
        token: currentUserToken,
        input: {
          name: newName
        }
      },
      update: (cache, { data }) => {
        const existingTasks = cache.readQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          }
        });
        const updatedTasks = existingTasks.tasks.map(task => {
          if (task.id === taskId) {
            return { ...task, taskName: newName };
          }
          return task;
        });
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: updatedTasks }
        });
      }
    }).catch(error => {
      if (
        error.message.includes('version_conflict_engine_exception') &&
        error.message.includes('version conflict')
      ) {
      } else {
        console.error(error);
      }
    });
  }  

  function getMessage() {
    const percentage = numberTotal === 0 ? 0 : (numberComplete / numberTotal) * 100;
    if (percentage === 0) {
      return 'no tasks complete ðŸ˜¾';
    }
    if (percentage === 100) {
      return 'nice job! ðŸ˜»';
    }
    return 'keep it up! ðŸ˜¼';
  }

  function handleLogout(){
    googleLogout()
    localStorage.setItem('currentUserImg', JSON.stringify(null));
    localStorage.setItem('currentUserName', JSON.stringify(null));
    localStorage.setItem('currentUser', JSON.stringify(null));
    localStorage.setItem('tasks', JSON.stringify(null));

    navigate('/login');
  }

  function handle(){
    setOpen(!open)
  }

  function handleExport() {
    const tasksJson = JSON.stringify(tasks);
    const blob = new Blob([tasksJson], { type: "application/json" });

    // Apre la finestra di dialogo per il salvataggio del file
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "tasks.json";
    link.click();
  }


  function Navbar(props) {
    return (
      <div>
        <nav className="nav">
          <ul className="nav-nav">
            {props.children}
            <li className="nav-item">
              <button className="iconButton" onClick={handleExport}>
                {exportIcon}
              </button>
            </li>
          </ul>
          <p className="user">Ciao {currentUserName}</p>
        </nav>
      </div>
    );
  }


  function Navitem(props) {
    return (
      <li className="nav-item">
        <a href="#" className="iconButton" onClick={handle}>
          <img src={currentUserImg} className="UserImg" alt="Immagine del profilo" />
          {props.icon}
        </a>
        {open && props.children}
      </li>
    );
  }

  function DropdownMenu() {
    const dropdownClass = open ? "dropdown dropdown-open" : "dropdown";
    function DropdownItem(props) {
      return (
        <a href="#" className="menu-item">
          {props.children}
        </a>
      );
    }
    return (
      <div className={dropdownClass} ref={ref}>
        <DropdownItem>
          <button className="out" onClick={handleLogout}>
            Disconnettiti
          </button>
        </DropdownItem>
      </div>
    );
  }

//{tasksData && tasksData.tasks && tasksData.tasks.map((task, index) => (
  return (
    <main>
    <Navbar>
      <Navitem>
        <DropdownMenu/>
      </Navitem>
    </Navbar>
      <h1>{numberComplete}/{numberTotal} Complete</h1>
      <h2>{getMessage()}</h2>
      <TaskForm onAdd={AddTask}/>
      {tasks.map((task, index) => (
        <Task
          {...task}
          key={index}
          onRename={newName => renameTasks(index, newName)}
          onTrash={() => removeTask(index)}
          onToggle={done => updateTaskDone(index, done)}
        />
      ))}
    </main>
  )
}

export default App;
