      id
    }
  }
`;

export const TASK_SORTED_NOT_DONE = gql`
  mutation getSortedByNotDone($token: String!) {
    getSortedByNotDone(token: $token) {
      userName
      token
      taskName
      done
      id
    }
  }
`;
