import { useNavigate } from 'react-router-dom';
import { CHECK_USER,GET_TASKS } from '../../queries/query';
import {useQuery,useMutation} from '@apollo/client'

const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));


async function HandleDeleteAccount(){
    const [deleteUserMutation] = useMutation(DELETE_USER)
    const { data: tasksData } = useQuery(GET_TASKS,{    
      variables: {
      token: currentUserToken,
      userName: currentUserName
    }
    });

    const navigate = useNavigate();
    const taskIds = tasksData.tasks.map((task) => task.id);
    const token = currentUserToken

    deleteUserMutation({
      variables: {
        token: currentUserToken,
        id: taskIds
      },
      update: (cache) => {
        cache.writeQuery({
          query: CHECK_USER,
          variables: {token:token},
          data: { CheckUser: {} },
        });
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: [] }, 
        });
      },
    })
    localStorage.setItem('currentUser', JSON.stringify(null));
    localStorage.setItem('currentUserName', JSON.stringify(null));
    localStorage.setItem('currentUserImg', JSON.stringify(null));
    navigate('/login')
  }


  export default HandleDeleteAccount
