import { googleLogout } from '@react-oauth/google';
import { useState } from "react";
import { useNavigate } from 'react-router-dom';
const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));
const currentUserImg = JSON.parse(localStorage.getItem('currentUserImg'));

function Navbar(props) {
    const [open, setOpen] = useState(false);

    return (
      <div>
        <nav className="nav">
          <ul className="nav-nav">{props.children}</ul>
          <p className="user">Ciao {currentUserName}</p>
        </nav>
      </div>
    );
  }

  function Navitem(props) {
    return (
      <li className="nav-item">
        <a href="#" className="iconButton" onClick={handle}>
          <img src={currentUserImg} className="UserImg" alt="Immagine del profilo" />
          {props.icon}
        </a>
        {open && props.children}
      </li>
    );
  }

  function handle(){
    if(!open){
      setOpen(true)}else{setOpen(false)}
  }

  function handleLogout(){
    googleLogout()
    localStorage.setItem('currentUserImg', JSON.stringify(null));
    localStorage.setItem('currentUserName', JSON.stringify(null));
    localStorage.setItem('currentUser', JSON.stringify(null));
    localStorage.setItem('tasks', JSON.stringify(null));

    navigate('/login');
  }

  function DropdownMenu(){
    const [activeMenu,setActiveMenu] = useState('main')
    function DropdownItem(props){
       return(
        <a href='#' className='menu-item'>
          {props.children}
        </a>
      )
    }
    return(
      <div className='dropdown'>
        <DropdownItem><button className="out" onClick={handleLogout}>Disconnettiti</button></DropdownItem>
      </div>
    )
  }

  export { Navbar, Navitem, DropdownMenu }
