import  React from 'react';
import  {GET_TASKS,DELETE_TASKS,DELETE_USER,CHECK_USER,TASK_SORTED_NOT_DONE} from '../../queries/query'
import  {useEffect,useState,useRef} from "react";
import  {ReactComponent as ExpoButton}  from '../icons/ExpoButton.svg'
import  {ReactComponent as DeleteAll} from '../icons/DeleteAll.svg'
import  {ReactComponent as FilterButton} from '../icons/FilterButton.svg'
import  {CSSTransition} from 'react-transition-group'
import  {ReactComponent as AtoZ}  from '../icons/AtoZ.svg'
import  {ReactComponent as ZtoA} from '../icons/ZtoA.svg'
import  {ReactComponent as Completed} from '../icons/Done.svg'
import  {ReactComponent as NotCompleted} from '../icons/NotDone.svg'
import  {ReactComponent as Logout} from '../icons/logout.svg'
import  {ReactComponent as GoBack} from '../icons/GoBack.svg'
import  {ReactComponent as DeleteUser} from '../icons/DeleteUser.svg'
import  {ReactComponent as Settings} from '../icons/Settings.svg'
import  {useQuery,useMutation} from '@apollo/client'
import  { useNavigate } from 'react-router-dom';



const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));


function DropdownMenu() {
  const [deleteAllTasksMutation] = useMutation(DELETE_TASKS)
  const [getTasksNotDone] = useMutation(TASK_SORTED_NOT_DONE)
  const [deleteUserMutation] = useMutation(DELETE_USER)

  const { data: tasksData } = useQuery(GET_TASKS,{    
    variables: {
    token: currentUserToken,
    userName: currentUserName
  }
  });

  async function RemoveAllTasks() {
    const taskIds = tasksData.tasks.map((task) => task.id);
    await deleteAllTasksMutation({
      variables: {
        token: currentUserToken,
        id: taskIds
      },
      update: (cache) => {
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: [] }, 
        });
      },
    });
  }

  async function HandleDeleteAccount(){
    const { data: tasksData } = useQuery(GET_TASKS,{    
      variables: {
      token: currentUserToken,
      userName: currentUserName
    }
    });

    const navigate = useNavigate();
    const taskIds = tasksData.tasks.map((task) => task.id);
    const token = currentUserToken

    deleteUserMutation({
      variables: {
        token: currentUserToken,
        id: taskIds
      },
      update: (cache) => {
        cache.writeQuery({
          query: CHECK_USER,
          variables: {token:token},
          data: { CheckUser: {} },
        });
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: [] }, 
        });
      },
    })
    localStorage.setItem('currentUser', JSON.stringify(null));
    localStorage.setItem('currentUserName', JSON.stringify(null));
    localStorage.setItem('currentUserImg', JSON.stringify(null));
    navigate('/login')
  }

  function HandleExport() {
    const tasks = JSON.parse(localStorage.getItem('tasks'));
    const tasksJson = JSON.stringify(tasks);
    const blob = new Blob([tasksJson], { type: "application/json" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "tasks.json";
    link.click();
  }

  function HandleLogout(){
    const navigate = useNavigate();
    googleLogout()
    localStorage.setItem('currentUserImg', JSON.stringify(null));
    localStorage.setItem('currentUserName', JSON.stringify(null));
    localStorage.setItem('currentUser', JSON.stringify(null));
    localStorage.setItem('tasks', JSON.stringify(null));

    navigate('/login');
  }

  function HandleNotDone() {
    const [sortedTasks, setSortedTasks] = useState([]);

      getTasksNotDone({
        variables: {
          token: currentUserToken,
          userName: currentUserName
        },
        update: (cache, { data }) => {
          const sorted = data.getSortedByNotDone;
          setSortedTasks(sorted); 
          cache.writeQuery({
            query: GET_TASKS,
            variables: {
              token: currentUserToken,
              userName: currentUserName
            },
            data: { tasks: sorted }
          });
        }
      })
    }

    const [activeMenu,setActiveMenu] = useState('main')
    const [menuHeight,setMenuHeight] = useState(null)
    const dropdownRef = useRef(null);

    useEffect(() => {
      setMenuHeight(dropdownRef.current?.firstChild.offsetHeight)
    }, [])


    function calcHeight(el) {
      const height = el.offsetHeight;
      setMenuHeight(height);
    }

    function DropdownItem(props) {
      const onClick = (event) => {
        event.preventDefault();
        if (props.goToMenu) {
          setActiveMenu(props.goToMenu);
        }
        if (props.onClick) {
          props.onClick();
        }
      };

      return (
        <a href="#" className="menu-item" onClick={onClick}>
          <span className="icon-button">{props.leftIcon}</span>
          {props.children}
          <span className="icon-right">{props.rightIcon}</span>
        </a>
      );
    }



    return (
      <div className="dropdown" style={{ height: menuHeight }} ref={dropdownRef}>

        <CSSTransition
          in={activeMenu === 'main'}
          timeout={500}
          classNames="menu-primary"
          unmountOnExit
          onEnter={calcHeight}>
          <div className="menu">
            <DropdownItem 
              rightIcon={<DeleteAll/>}
              onClick={RemoveAllTasks}>
              Elimina Tutto
              </DropdownItem>
            <DropdownItem
              rightIcon={<ExpoButton/>}
              onClick={HandleExport}>
              Esporta JSON
            </DropdownItem>
            <DropdownItem
              rightIcon={<FilterButton/>}
              goToMenu="filtri">
              Filtra
            </DropdownItem>
            <DropdownItem
              rightIcon={<Settings/>}
              goToMenu="settings">
              Impostazioni
            </DropdownItem>
            <DropdownItem
              rightIcon={<Logout/>}
              onClick={HandleLogout}>
              Cambia Account
            </DropdownItem>

          </div>
        </CSSTransition>

        <CSSTransition
        in={activeMenu === 'filtri'}
        timeout={500}
        classNames="menu-secondary"
        unmountOnExit
        onEnter={calcHeight}>
        <div className="menu">
          <DropdownItem
          rightIcon={<AtoZ/>}
          onClick={HandleSorted}
          > 
          Da A a Z
          </DropdownItem>
          <DropdownItem
          rightIcon={<ZtoA/>}
          onClick={HandleSortedDesc}
          > 
          Da Z a A
          </DropdownItem>
          <DropdownItem
          rightIcon={<Completed/>}
          onClick={HandleSortedDone}
          > Completate
          </DropdownItem>
          <DropdownItem
          rightIcon={<NotCompleted/>}
          onClick={HandleNotDone}
          > 
          Da completare
          </DropdownItem>
          <DropdownItem 
          goToMenu="main"
          rightIcon={<GoBack/>}>
          Torna indietro
          </DropdownItem>

        </div>
      </CSSTransition>

      <CSSTransition
        in={activeMenu === 'settings'}
        timeout={500}
        classNames="menu-secondary"
        unmountOnExit
        onEnter={calcHeight}>
        <div className="menu">
          <DropdownItem
          rightIcon={<DeleteUser/>}
          onClick={HandleDeleteAccount}> 
          Elimina account
          </DropdownItem>
          <DropdownItem 
          goToMenu="main"
          rightIcon={<GoBack/>}>
          Torna indietro
          </DropdownItem>
        </div>
      </CSSTransition>

      </div>
    );
  }

  export default DropdownMenu
