import { TASK_SORTED_NOT_DONE,GET_TASKS } from "../../queries/query";
import  { useState } from "react";
import  { useMutation } from '@apollo/client'

const currentUserToken = JSON.parse(localStorage.getItem('currentUser'));
const currentUserName = JSON.parse(localStorage.getItem('currentUserName'));

function HandleNotDone() {
  const [sortedTasks, setSortedTasks] = useState([]);
  const [getTasksNotDone] = useMutation(TASK_SORTED_NOT_DONE)

    getTasksNotDone({
      variables: {
        token: currentUserToken,
        userName: currentUserName
      },
      update: (cache, { data }) => {
        const sorted = data.getSortedByNotDone;
        setSortedTasks(sorted); 
        cache.writeQuery({
          query: GET_TASKS,
          variables: {
            token: currentUserToken,
            userName: currentUserName
          },
          data: { tasks: sorted }
        });
      }
    })
  }

  export default HandleNotDone
