import { GoogleLogin, GoogleOAuthProvider } from '@react-oauth/google';
import jwt_decode from 'jwt-decode';
import { useState, useEffect } from 'react';
import './components.css';
import { useNavigate } from 'react-router-dom';
import  {gql,useMutation} from '@apollo/client'
import client from '../index'

const CHECK_USER = gql`
  query CheckUser($token: String!) {
    CheckUser(token: $token) {
      _id
      userName
    }
  }
`;

const CREATE_USER = gql`
  mutation newUser($token: String!, $name: String!, $email:String!) {
    newUser(token: $token, name: $name, email:$email){
      _id
      userName
    }
  }
`;

export default function Login() {
    const navigate = useNavigate();
    const [addUserMutation] = useMutation(CREATE_USER);
    const [currentUserToken, setCurrentUserToken] = useState(null);
    const [currentUserName, setCurrentUserName] = useState(null);
    const [currentUserImg, setCurrentUserImg] = useState(null);
    const [isRegistered, setIsRegistered] = useState(false); 

    useEffect(() => {
      console.log('user:' + currentUserToken);
    }, []);

    function NavigateTo() {
      navigate('/login');
    }

    const handleSuccess = async (credentialResponse) => {
      var decoded = jwt_decode(credentialResponse.credential);
      const token = decoded.sub;
      const { data } = await client.query({
        query: CHECK_USER,
        variables: { token: token }
      });
      console.log(data);
      if (!data.CheckUser) {
        await addUserMutation({
          variables: {
            token: token,
            name: decoded.name,
            email: decoded.email
          },
          update(cache, { data }) {
            const newUserData = {
              _id: data.newUser._id,
              userName: data.newUser.userName,
            }
            console.log(data);
            const updated = {
              CheckUser: { ...data.CheckUser, ...newUserData }
            };

            client.writeQuery({
              query: CHECK_USER,
              variables: { token: token },
              data: updated
            });
          }
        });
        localStorage.setItem('currentUser', JSON.stringify(decoded.sub));
        localStorage.setItem('currentUserName', JSON.stringify(decoded.given_name));
        localStorage.setItem('currentUserImg', JSON.stringify(decoded.picture));
        navigate('/');
      } else {
        setIsRegistered(true);
      }
    };

    if (isRegistered) {
      return (
        <main className='registerpage'>
          <div>
            <h1>Mhh...</h1>
            <h2>This account seems to be registered already</h2>
            <h2>Move to <a href="#" className='redirectRegister' onClick={NavigateTo}>Login Page!</a></h2>
          </div>
        </main>
      );
    } else {
      return (
        <div id="signInButton" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          <GoogleOAuthProvider clientId="1038509275601-a2gf0vtu5pt81as8586gt0hsqrqis0mh.apps.googleusercontent.com">
            <GoogleLogin onSuccess={handleSuccess}/>
          </GoogleOAuthProvider>
        </div>
      );
    }
  }
