import { GoogleLogin, GoogleOAuthProvider } from '@react-oauth/google';
import jwt_decode from 'jwt-decode';
import { useState, useEffect } from 'react';
import './components.css';
import { useNavigate } from 'react-router-dom';
import {useQuery,gql,useMutation} from '@apollo/client'

const GET_TASKS = gql`
  query tasks {
    tasks {
      name
      done
      id
    }
  }
`;

const GET_USER = gql`
  query users {
    users {
      name
      token
      tasks{
        name
        done
        id
      }
    }
  }
`;

const NEW_USER = gql`
  mutation newUser($token:String! $name:String! $tasks:[userInput]) {
    newUser(token:$token name:$name tasks:$tasks) {
      name
      token
      tasks{
      name
      done
      id
    }
    }
  }
`;

export default function Login() {
  const navigate = useNavigate();
  const { data: tasksData } = useQuery(GET_TASKS)
  const { data: userData} = useQuery(GET_USER)
  const [newUserMutation] = useMutation(NEW_USER)
  const [currentUserToken, setCurrentUserToken] = useState(null);
  const [currentUserName, setCurrentUserName] = useState(null);
  const [currentUserImg, setCurrentUserImg] = useState(null);

  const users = userData?.users ?? []; 

  useEffect(() => {
    console.log('user:' + currentUserToken);
  },);


  const handleSuccess = async (credentialResponse) => {
    var decoded = jwt_decode(credentialResponse.credential);
    const users = userData?.users ?? []; 
    const tasks = JSON.parse(localStorage.getItem('tasks')) || [];
    const existingUser = users.find((users) => users.token === decoded.sub);      //chatgpt
    if (!existingUser) {
      const tasksWithoutTypename = tasks.map(({ __typename, ...rest }) => rest);  //chatgpt
      await newUserMutation({
        variables: {
          token: decoded.sub,
          name: decoded.name,
          tasks: tasksWithoutTypename,
        },
        update: (cache, { data }) => {
          const existingUser = cache.readQuery({ query: GET_USER });
          const newUser = {
            token: data.newUser.token,
            name: data.newUser.name,
            done: false,
            tasks: data.newUser.tasks,
          };
          const updatedUser = [...existingUser.users, newUser];
          cache.writeQuery({
            query: GET_USER,
            data: { users: updatedUser }
          });
        }
      });
    }
    setCurrentUserToken(decoded.sub);
    setCurrentUserName(decoded.given_name);
    setCurrentUserImg(decoded.picture);
    localStorage.setItem('currentUser', JSON.stringify(decoded.sub));
    navigate('/');
  };



  const handleError = () => {
    console.log('Login Failed');
  };



  return (
    <div id="signInButton">
        <GoogleOAuthProvider clientId="1038509275601-a2gf0vtu5pt81as8586gt0hsqrqis0mh.apps.googleusercontent.com">
          <GoogleLogin onSuccess={handleSuccess} onError={handleError} />
        </GoogleOAuthProvider>
    </div>
  );
}
