import { GoogleLogin, GoogleOAuthProvider } from '@react-oauth/google';
import jwt_decode from 'jwt-decode';
import { useState, useEffect } from 'react';
import './components.css';
import { useNavigate } from 'react-router-dom';
import {useQuery,gql,useMutation} from '@apollo/client'

const GET_TASKS = gql`
  query tasks {
    tasks {
      name
      done
      id
    }
  }
`;

const GET_USER = gql`
  query users {
    users {
      name
      token
      isLoggedIn
    }
  }
`;

const NEW_USER = gql`
  mutation newUser($token:String! $name:String! $isLoggedIn:Boolean! $tasks:userInput) {
    newUser(token:$token name:$name tasks:$tasks isLoggedIn:$isLoggedIn) {
      name
      tasks {
      name
      done
      id
    }
      token
      isLoggedIn
    }
  }
`;

export default function Login() {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const { data: tasksData } = useQuery(GET_TASKS)
  const { data: userData} = useQuery(GET_USER)
  const [newUserMutation] = useMutation(NEW_USER)

  const handleSuccess = async (credentialResponse) => {
    var decoded = jwt_decode(credentialResponse.credential);
    const storedUsers = JSON.parse(localStorage.getItem('users')) || [];
    const tasks = JSON.parse(localStorage.getItem('tasks')) || [];
    const user = {
      name: decoded.name,
      token: decoded.sub,
      isLoggedIn: true,
      tasks: tasks,
    };
    const existingUser = storedUsers.find((storedUser) => storedUser.token === decoded.sub);
    if (!existingUser) {
        await newUserMutation({
          variables: {
            token: decoded.sub,
            name: decoded.name,
            isLoggedIn: true,
            tasks:tasks,
          }
        });
        storedUsers.push(user);
        localStorage.setItem('users', JSON.stringify(storedUsers));
      }else {
      setIsLoggedIn(true);
    }

    navigate('/');
  };


  const handleError = () => {
    console.log('Login Failed');
    setIsLoggedIn(false);
  };



  return (
    <div id="signInButton">
        <GoogleOAuthProvider clientId="1038509275601-a2gf0vtu5pt81as8586gt0hsqrqis0mh.apps.googleusercontent.com">
          <GoogleLogin onSuccess={handleSuccess} onError={handleError} />
        </GoogleOAuthProvider>
    </div>
  );
}
